import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { useSocket } from '../contexts/SocketContext';
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.md};
  max-width: 900px;
`;

const Section = styled.section`
  background: ${props => props.theme.colors.surface};
  border-radius: ${props => props.theme.borderRadius.md};
  padding: ${props => props.theme.spacing.md};
  box-shadow: ${props => props.theme.shadows.sm};
`;

const SectionTitle = styled.h2`
  font-size: 0.75rem;
  font-weight: 600;
  color: ${props => props.theme.colors.textMuted};
  text-transform: uppercase;
  letter-spacing: 0.03em;
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const LiveStatus = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  margin-bottom: ${props => props.theme.spacing.md};
`;

const LiveDot = styled.span`
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: ${props => props.connected ? props.theme.colors.success : props.theme.colors.danger};
  animation: ${props => props.connected ? 'pulse 2s infinite' : 'none'};

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }
`;

const LiveText = styled.span`
  font-size: 0.85rem;
  color: ${props => props.theme.colors.text};
  font-weight: 500;
`;

const UnitsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: ${props => props.theme.spacing.xs};

  @media (max-width: 768px) {
    grid-template-columns: repeat(3, 1fr);
  }

  @media (max-width: 480px) {
    grid-template-columns: repeat(2, 1fr);
  }
`;

const UnitCard = styled(Link)`
  background: ${props => props.theme.colors.background};
  border-radius: ${props => props.theme.borderRadius.sm};
  padding: ${props => props.theme.spacing.sm};
  text-decoration: none;
  text-align: center;
  border-left: 3px solid ${props => {
    if (props.status === 'critical') return props.theme.colors.danger;
    if (props.status === 'warning') return props.theme.colors.warning;
    return props.theme.colors.success;
  }};
  transition: all 0.15s;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
  }
`;

const UnitName = styled.div`
  font-size: 0.85rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  margin-bottom: 2px;
`;

const UnitMetric = styled.div`
  font-size: 0.7rem;
  color: ${props => props.theme.colors.textMuted};
`;

const RoomGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.sm};

  @media (max-width: 480px) {
    grid-template-columns: 1fr;
  }
`;

const RoomCard = styled.div`
  background: ${props => props.theme.colors.background};
  border-radius: ${props => props.theme.borderRadius.sm};
  padding: ${props => props.theme.spacing.sm};
`;

const RoomName = styled.div`
  font-size: 0.8rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  margin-bottom: ${props => props.theme.spacing.xs};
`;

const RoomMetrics = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: ${props => props.theme.spacing.sm};
`;

const Metric = styled.div`
  display: flex;
  align-items: baseline;
  gap: 4px;
`;

const MetricValue = styled.span`
  font-size: 1rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
`;

const MetricLabel = styled.span`
  font-size: 0.65rem;
  color: ${props => props.theme.colors.textMuted};
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.xl};
  color: ${props => props.theme.colors.textSecondary};
`;

const hydroUnits = [
  { id: 'DWC1', name: 'DWC 1' },
  { id: 'DWC2', name: 'DWC 2' },
  { id: 'NFT', name: 'NFT' },
  { id: 'AERO', name: 'Aero' },
  { id: 'TROUGH', name: 'Trough' }
];

function getUnitStatus(data) {
  if (!data?.reservoir) return 'unknown';
  const { reservoir } = data;
  if (reservoir.ph < 5.5 || reservoir.ph > 7.5 || reservoir.water_level < 20) return 'critical';
  if (reservoir.ph < 6.0 || reservoir.ph > 7.0 || reservoir.water_level < 50) return 'warning';
  return 'normal';
}

const Dashboard = () => {
  const [hydroUnitsData, setHydroUnitsData] = useState({});
  const [roomData, setRoomData] = useState({ front: null, back: null });
  const [loading, setLoading] = useState(true);
  const { connected } = useSocket();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const hydroPromises = hydroUnits.map(async (unit) => {
          const response = await axios.get(`/units/${unit.id}/sensors`);
          return { unitId: unit.id, data: response.data };
        });

        const [hydroResults, frontResponse, backResponse] = await Promise.all([
          Promise.all(hydroPromises),
          axios.get('/room/front/sensors'),
          axios.get('/room/back/sensors')
        ]);

        const hydroData = {};
        hydroResults.forEach(({ unitId, data }) => {
          hydroData[unitId] = data;
        });
        setHydroUnitsData(hydroData);

        setRoomData({
          front: frontResponse.data,
          back: backResponse.data
        });

      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return <LoadingMessage>Loading...</LoadingMessage>;
  }

  const unitsOnline = Object.keys(hydroUnitsData).length;

  return (
    <Container>
      {/* Live Status */}
      <LiveStatus>
        <LiveDot connected={connected} />
        <LiveText>{unitsOnline}/{hydroUnits.length} Racks Live</LiveText>
      </LiveStatus>

      {/* Hydroponic Units */}
      <Section>
        <SectionTitle>Hydroponic Units</SectionTitle>
        <UnitsGrid>
          {hydroUnits.map((unit) => {
            const data = hydroUnitsData[unit.id];
            const status = getUnitStatus(data);

            return (
              <UnitCard key={unit.id} to={`/hydro-units/${unit.id}`} status={status}>
                <UnitName>{unit.name}</UnitName>
                <UnitMetric>
                  pH {data?.reservoir?.ph?.toFixed(1) || '-'} | {data?.reservoir?.water_level || '-'}%
                </UnitMetric>
              </UnitCard>
            );
          })}
        </UnitsGrid>
      </Section>

      {/* Room Environment */}
      <Section>
        <SectionTitle>Environment</SectionTitle>
        <RoomGrid>
          {/* Front Room */}
          <RoomCard>
            <RoomName>Front Room</RoomName>
            <RoomMetrics>
              <Metric>
                <MetricValue>{roomData.front?.bme?.temp?.toFixed(1) || '-'}°C</MetricValue>
              </Metric>
              <Metric>
                <MetricValue>{roomData.front?.bme?.humidity?.toFixed(0) || '-'}%</MetricValue>
                <MetricLabel>RH</MetricLabel>
              </Metric>
              <Metric>
                <MetricValue>{roomData.front?.co2 || '-'}</MetricValue>
                <MetricLabel>ppm</MetricLabel>
              </Metric>
            </RoomMetrics>
          </RoomCard>

          {/* Back Room */}
          <RoomCard>
            <RoomName>Back Room</RoomName>
            <RoomMetrics>
              <Metric>
                <MetricValue>{roomData.back?.bme?.temp?.toFixed(1) || '-'}°C</MetricValue>
              </Metric>
              <Metric>
                <MetricValue>{roomData.back?.bme?.humidity?.toFixed(0) || '-'}%</MetricValue>
                <MetricLabel>RH</MetricLabel>
              </Metric>
              <Metric>
                <MetricValue>{roomData.back?.co2 || '-'}</MetricValue>
                <MetricLabel>ppm</MetricLabel>
              </Metric>
              <Metric>
                <MetricValue>AC {roomData.back?.ac?.current_set_temp || '-'}°C</MetricValue>
              </Metric>
            </RoomMetrics>
          </RoomCard>
        </RoomGrid>
      </Section>
    </Container>
  );
};

export default Dashboard;
