import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  background: ${props => props.theme.colors.surface};
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.lg};
  box-shadow: ${props => props.theme.shadows.sm};
  margin-bottom: ${props => props.theme.spacing.md};
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: ${props => props.theme.spacing.md};
`;

const Label = styled.h3`
  font-size: 1.1rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  margin: 0;
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
`;

const ModeSwitch = styled.div`
  display: flex;
  background: ${props => props.theme.colors.background};
  border-radius: ${props => props.theme.borderRadius.md};
  padding: 4px;
`;

const ModeButton = styled.button`
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  border: none;
  border-radius: ${props => props.theme.borderRadius.sm};
  font-size: 0.875rem;
  font-weight: 600;
  cursor: pointer;
  transition: ${props => props.theme.transitions.default};
  background: ${props => props.active
    ? (props.mode === 'manual' ? props.theme.colors.warning : props.theme.colors.primary)
    : 'transparent'};
  color: ${props => props.active
    ? '#fff'
    : props.theme.colors.textMuted};

  &:hover {
    background: ${props => props.active
      ? (props.mode === 'manual' ? props.theme.colors.warning : props.theme.colors.primary)
      : props.theme.colors.surfaceHover};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const ControlsContainer = styled.div`
  margin-top: ${props => props.theme.spacing.md};
`;

/* Manual Mode Controls */
const ManualControls = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: ${props => props.theme.spacing.md};
  background: ${props => props.theme.colors.warning}10;
  border-radius: ${props => props.theme.borderRadius.md};
  border: 1px solid ${props => props.theme.colors.warning}30;
`;

const StateLabel = styled.span`
  font-size: 1rem;
  font-weight: 600;
  color: ${props => props.state === 'ON'
    ? props.theme.colors.success
    : props.theme.colors.textMuted};
`;

const ToggleSwitch = styled.button`
  position: relative;
  width: 60px;
  height: 30px;
  background: ${props => props.isOn
    ? props.theme.colors.success
    : props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.full};
  transition: ${props => props.theme.transitions.default};
  border: none;
  cursor: pointer;

  &:hover {
    background: ${props => props.isOn
      ? props.theme.colors.successHover
      : props.theme.colors.borderHover};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  &::after {
    content: '';
    position: absolute;
    top: 2px;
    left: ${props => props.isOn ? '32px' : '2px'};
    width: 26px;
    height: 26px;
    background: white;
    border-radius: 50%;
    transition: ${props => props.theme.transitions.default};
    box-shadow: ${props => props.theme.shadows.sm};
  }
`;

/* Timer Mode Controls */
const TimerControls = styled.div`
  padding: ${props => props.theme.spacing.md};
  background: ${props => props.theme.colors.primary}10;
  border-radius: ${props => props.theme.borderRadius.md};
  border: 1px solid ${props => props.theme.colors.primary}30;
`;

const InputRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.md};
  margin-bottom: ${props => props.theme.spacing.md};

  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    grid-template-columns: 1fr;
  }
`;

const InputGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.xs};
`;

const InputLabel = styled.label`
  font-size: 0.875rem;
  font-weight: 500;
  color: ${props => props.theme.colors.textSecondary};
`;

const Input = styled.input`
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.md};
  font-size: 1rem;
  background: ${props => props.theme.colors.surface};
  color: ${props => props.theme.colors.text};

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
  }
`;

const HelpText = styled.div`
  font-size: 0.75rem;
  color: ${props => props.theme.colors.textMuted};
`;

const PreviewBox = styled.div`
  background: ${props => props.theme.colors.background};
  border-radius: ${props => props.theme.borderRadius.sm};
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  margin-bottom: ${props => props.theme.spacing.md};
  font-size: 0.875rem;
  color: ${props => props.theme.colors.text};
`;

const ButtonRow = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: ${props => props.theme.spacing.sm};
`;

const Button = styled.button`
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.lg};
  border-radius: ${props => props.theme.borderRadius.md};
  font-weight: 600;
  font-size: 0.875rem;
  cursor: pointer;
  transition: ${props => props.theme.transitions.default};

  ${props => props.variant === 'primary' && `
    background: ${props.theme.colors.primary};
    color: white;
    border: none;

    &:hover { background: ${props.theme.colors.primaryHover}; }
    &:disabled { background: ${props.theme.colors.textMuted}; cursor: not-allowed; }
  `}

  ${props => props.variant === 'secondary' && `
    background: transparent;
    color: ${props.theme.colors.textSecondary};
    border: 1px solid ${props.theme.colors.border};

    &:hover { background: ${props.theme.colors.surfaceHover}; }
  `}
`;

const LoadingSpinner = styled.div`
  width: 20px;
  height: 20px;
  border: 2px solid ${props => props.theme.colors.border};
  border-top: 2px solid ${props => props.theme.colors.primary};
  border-radius: 50%;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

function formatDuration(seconds) {
  if (seconds < 60) return `${seconds}s`;
  if (seconds < 3600) return `${Math.floor(seconds / 60)}m ${seconds % 60}s`;
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  return `${hours}h ${minutes}m`;
}

const PumpControl = ({
  state,
  controlMode,
  pumpCycle,
  onRelayToggle,
  onModeChange,
  onCycleUpdate,
  disabled = false
}) => {
  const [loading, setLoading] = useState(false);
  const [modeLoading, setModeLoading] = useState(false);
  const [duration, setDuration] = useState(pumpCycle?.on_duration_sec || 300);
  const [interval, setIntervalVal] = useState(pumpCycle?.interval_sec || 3600);

  const isOn = state === 'ON';
  const hasChanges = duration !== (pumpCycle?.on_duration_sec || 300) ||
                     interval !== (pumpCycle?.interval_sec || 3600);

  const handleToggle = async () => {
    if (disabled || loading) return;
    setLoading(true);
    try {
      await onRelayToggle(isOn ? 'OFF' : 'ON');
    } catch (error) {
      console.error('Error toggling pump:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleModeChange = async (newMode) => {
    if (disabled || modeLoading || newMode === controlMode) return;
    setModeLoading(true);
    try {
      await onModeChange(newMode);
    } catch (error) {
      console.error('Error changing mode:', error);
    } finally {
      setModeLoading(false);
    }
  };

  const handleCycleSave = async () => {
    if (!hasChanges || loading) return;
    setLoading(true);
    try {
      await onCycleUpdate({
        on_duration_sec: duration,
        interval_sec: interval
      });
    } catch (error) {
      console.error('Error saving pump cycle:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setDuration(pumpCycle?.on_duration_sec || 300);
    setIntervalVal(pumpCycle?.interval_sec || 3600);
  };

  return (
    <Container>
      <Header>
        <Label>ðŸ’§ Pump</Label>
        <ModeSwitch>
          <ModeButton
            active={controlMode === 'manual'}
            mode="manual"
            onClick={() => handleModeChange('manual')}
            disabled={modeLoading}
          >
            Manual
          </ModeButton>
          <ModeButton
            active={controlMode === 'timer'}
            mode="timer"
            onClick={() => handleModeChange('timer')}
            disabled={modeLoading}
          >
            Timer
          </ModeButton>
        </ModeSwitch>
      </Header>

      <ControlsContainer>
        {controlMode === 'manual' ? (
          <ManualControls>
            <div>
              <StateLabel state={state}>{state}</StateLabel>
              <div style={{ fontSize: '0.75rem', color: '#888', marginTop: '4px' }}>
                Direct control - toggle manually
              </div>
            </div>
            {loading ? (
              <LoadingSpinner />
            ) : (
              <ToggleSwitch
                isOn={isOn}
                onClick={handleToggle}
                disabled={disabled}
              />
            )}
          </ManualControls>
        ) : (
          <TimerControls>
            <InputRow>
              <InputGroup>
                <InputLabel>Run Duration (seconds)</InputLabel>
                <Input
                  type="number"
                  min="1"
                  max="3600"
                  value={duration}
                  onChange={(e) => setDuration(parseInt(e.target.value) || 0)}
                  disabled={disabled || loading}
                />
                <HelpText>How long pump runs (1-3600s)</HelpText>
              </InputGroup>
              <InputGroup>
                <InputLabel>Cycle Interval (seconds)</InputLabel>
                <Input
                  type="number"
                  min="60"
                  max="86400"
                  value={interval}
                  onChange={(e) => setIntervalVal(parseInt(e.target.value) || 0)}
                  disabled={disabled || loading}
                />
                <HelpText>Time between cycles (60-86400s)</HelpText>
              </InputGroup>
            </InputRow>

            <PreviewBox>
              Pump runs for <strong>{formatDuration(duration)}</strong> every{' '}
              <strong>{formatDuration(interval)}</strong>
              {interval > 0 && duration > 0 && (
                <span> ({Math.round((duration / interval) * 100 * 100) / 100}% duty)</span>
              )}
            </PreviewBox>

            <ButtonRow>
              {hasChanges && (
                <Button variant="secondary" onClick={handleReset} disabled={loading}>
                  Reset
                </Button>
              )}
              <Button
                variant="primary"
                onClick={handleCycleSave}
                disabled={!hasChanges || loading || duration <= 0 || interval < 60}
              >
                {loading ? 'Saving...' : 'Save Cycle'}
              </Button>
            </ButtonRow>
          </TimerControls>
        )}
      </ControlsContainer>
    </Container>
  );
};

export default PumpControl;
