import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import AlertsBanner from '../components/AlertsBanner';
import QuickStats from '../components/QuickStats';
import UnitCard from '../components/UnitCard';
import EnvironmentRow from '../components/EnvironmentRow';
import { useSocket } from '../contexts/SocketContext';
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.md};
`;

const Section = styled.section`
  background: ${props => props.theme.colors.surface};
  border-radius: ${props => props.theme.borderRadius.md};
  padding: ${props => props.theme.spacing.md};
  box-shadow: ${props => props.theme.shadows.sm};
`;

const SectionTitle = styled.h2`
  font-size: 0.75rem;
  font-weight: 600;
  color: ${props => props.theme.colors.textMuted};
  text-transform: uppercase;
  letter-spacing: 0.03em;
  margin-bottom: ${props => props.theme.spacing.sm};
`;

const UnitsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
  gap: ${props => props.theme.spacing.sm};
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: ${props => props.theme.spacing.xl};
  color: ${props => props.theme.colors.textSecondary};
`;

const hydroUnits = [
  { id: 'DWC1', type: 'Deep Water Culture' },
  { id: 'DWC2', type: 'Deep Water Culture' },
  { id: 'NFT', type: 'NFT System' },
  { id: 'AERO', type: 'Aeroponic' },
  { id: 'TROUGH', type: 'Trough System' }
];

function getUnitStatus(sensorData) {
  if (!sensorData?.reservoir) return 'unknown';
  const { reservoir } = sensorData;

  // Critical
  if (reservoir.ph < 5.5 || reservoir.ph > 7.5) return 'critical';
  if (reservoir.tds < 500 || reservoir.tds > 1500) return 'critical';
  if (reservoir.water_level < 20) return 'critical';

  // Warning
  if (reservoir.ph < 6.0 || reservoir.ph > 7.0) return 'warning';
  if (reservoir.tds < 700 || reservoir.tds > 1300) return 'warning';
  if (reservoir.water_level < 50) return 'warning';

  return 'normal';
}

function generateAlerts(hydroUnitsData) {
  const alerts = [];
  const warnings = [];

  Object.entries(hydroUnitsData).forEach(([unitId, data]) => {
    if (!data?.reservoir) return;
    const { reservoir } = data;

    // pH checks
    if (reservoir.ph < 5.5) {
      alerts.push({ unitId, message: 'pH too low', value: reservoir.ph.toFixed(1) });
    } else if (reservoir.ph > 7.5) {
      alerts.push({ unitId, message: 'pH too high', value: reservoir.ph.toFixed(1) });
    } else if (reservoir.ph < 6.0 || reservoir.ph > 7.0) {
      warnings.push({ unitId, message: 'pH out of optimal range', value: reservoir.ph.toFixed(1) });
    }

    // Water level checks
    if (reservoir.water_level < 20) {
      alerts.push({ unitId, message: 'Water level critical', value: `${reservoir.water_level}%` });
    } else if (reservoir.water_level < 50) {
      warnings.push({ unitId, message: 'Water level low', value: `${reservoir.water_level}%` });
    }

    // TDS checks
    if (reservoir.tds < 500 || reservoir.tds > 1500) {
      alerts.push({ unitId, message: 'TDS out of range', value: `${reservoir.tds} ppm` });
    } else if (reservoir.tds < 700 || reservoir.tds > 1300) {
      warnings.push({ unitId, message: 'TDS not optimal', value: `${reservoir.tds} ppm` });
    }
  });

  return { alerts, warnings };
}

const Dashboard = () => {
  const [hydroUnitsData, setHydroUnitsData] = useState({});
  const [relayData, setRelayData] = useState({});
  const [roomData, setRoomData] = useState({ front: null, back: null });
  const [loading, setLoading] = useState(true);
  const { connected } = useSocket();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // Fetch hydro units sensor data
        const hydroPromises = hydroUnits.map(async (unit) => {
          const response = await axios.get(`/units/${unit.id}/sensors`);
          return { unitId: unit.id, data: response.data };
        });

        // Fetch relay data for each unit
        const relayPromises = hydroUnits.map(async (unit) => {
          try {
            const response = await axios.get(`/units/${unit.id}/relays`);
            return { unitId: unit.id, data: response.data };
          } catch {
            return { unitId: unit.id, data: null };
          }
        });

        const [hydroResults, relayResults, frontResponse, backResponse] = await Promise.all([
          Promise.all(hydroPromises),
          Promise.all(relayPromises),
          axios.get('/room/front/sensors'),
          axios.get('/room/back/sensors')
        ]);

        // Process hydro data
        const hydroData = {};
        hydroResults.forEach(({ unitId, data }) => {
          hydroData[unitId] = data;
        });
        setHydroUnitsData(hydroData);

        // Process relay data
        const relays = {};
        relayResults.forEach(({ unitId, data }) => {
          relays[unitId] = data?.relays || {};
        });
        setRelayData(relays);

        // Set room data
        setRoomData({
          front: frontResponse.data,
          back: backResponse.data
        });

      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return <LoadingMessage>Loading dashboard...</LoadingMessage>;
  }

  const { alerts, warnings } = generateAlerts(hydroUnitsData);

  // Calculate averages
  const phValues = Object.values(hydroUnitsData)
    .filter(d => d?.reservoir?.ph)
    .map(d => d.reservoir.ph);
  const avgPh = phValues.length > 0 ? phValues.reduce((a, b) => a + b, 0) / phValues.length : 0;

  const frontTemp = roomData.front?.bme?.temp;
  const frontHumidity = roomData.front?.bme?.humidity;

  return (
    <Container>
      {/* Alerts Banner - only shows if there are issues */}
      <AlertsBanner alerts={alerts} warnings={warnings} />

      {/* Quick Stats Row */}
      <QuickStats
        unitsOnline={Object.keys(hydroUnitsData).length}
        totalUnits={hydroUnits.length}
        alertCount={alerts.length}
        warningCount={warnings.length}
        avgPh={avgPh}
        roomTemp={frontTemp}
        roomHumidity={frontHumidity}
        connected={connected}
      />

      {/* Hydro Units Grid */}
      <Section>
        <SectionTitle>Hydroponic Units</SectionTitle>
        <UnitsGrid>
          {hydroUnits.map((unit) => {
            const data = hydroUnitsData[unit.id];
            const relays = relayData[unit.id] || {};
            const status = getUnitStatus(data);

            return (
              <UnitCard
                key={unit.id}
                unitId={unit.id}
                type={unit.type}
                status={status}
                ph={data?.reservoir?.ph}
                tds={data?.reservoir?.tds}
                waterTemp={data?.reservoir?.water_temp}
                waterLevel={data?.reservoir?.water_level}
                relays={relays}
              />
            );
          })}
        </UnitsGrid>
      </Section>

      {/* Environment Row */}
      <EnvironmentRow
        frontRoom={roomData.front ? {
          temp: roomData.front.bme?.temp,
          humidity: roomData.front.bme?.humidity,
          co2: roomData.front.co2
        } : null}
        backRoom={roomData.back ? {
          temp: roomData.back.bme?.temp,
          humidity: roomData.back.bme?.humidity,
          acTemp: roomData.back.ac?.current_set_temp
        } : null}
      />
    </Container>
  );
};

export default Dashboard;
